function isCheck(empty, check, radio1, radio2)	
            {
              return (check == "true" && (radio1 == "true" || radio2 == "true")) ? "true" : "false"; 
            }

function isCheck(empty, radio1, radio2, check)	
            {
              return ((radio1 == "true" || radio2 == "true") && check == "true") ? "true" : "false"; 
            }

function isCheck(empty, radio1, radio2, check1, check2, check3)	
            {
              return ((radio1 == "true" || radio2 == "true") && (check1 == "true" || check2 == "true" || check3 == "true")) ? "true" : "false"; 
            }

