function isCheck(empty, check1, check2, check3)	
            {
              ret = "";
              if(check1 == "true")  ret += ",증권담보융자";
              if(check2 == "true")  ret += ",매도증권담보융자";
              if(check3 == "true")  ret += ",청약자금대출";
              return ret.substring(1);
            }

