 function setValue(value) 
      {
        if(value == "01") return "true|false|false|false|";
        if(value == "07") return "false|true|false|false|";
        if(value == "02") return "false|false|true|false|";
        if(value == "05") return "false|false|false|true|";

        return "false|false|false|false|";
      }





 function setValue(value) 
      {
        if(value == "1") return "true|false|false";
        if(value == "5") return "false|true|false";
        if(value == "2") return "false|false|true";

        return "false|false|false";
      }
















































 function setValue(value)
      {
        if(value == "00") return "true|true|true|true|true";

        var ret = "false|";
        ret += (value.search('20')!=-1) ? "true|" : "false|";
        ret += (value.search('30')!=-1) ? "true|" : "false|";
        ret += (value.search('10')!=-1) ? "true|" : "false|";
        ret += (value.search('40')!=-1) ? "true" : "false";
        return ret;
      }





 function setValue(value)
      {
        if(value == "3")  return "true|false";
        if(value == "1" || value == "2")  return "false|true";
        return "false|false";
      }





























 function setValue(value)
      {
        var ret = "";
        var year = parseInt(Number(value)/12);
        var month = Number(value)%12;
        ret = year.toString() + "|" + month.toString();

        return ret;
      }



































































































































































































 function setValue(value) 
      { 
        var values = value.split("/");
        var ret = "";

        ret += (values[0] == "0") ? "false|" : "true|";
        ret += (values[1] == "0") ? "false" : "true";
        return ret;
      }






 function setValue(value) 
      { 
        var values = value.split("/");
        var ret = "";

        ret += (values[0] == "0") ? "false|" : "true|";
        ret += (values[1] == "0") ? "false|" : "true|";
        ret += (values[2] == "0") ? "false|" : "true|";
        ret += (values[3] == "0") ? "false" : "true";
        return ret;
      }




















































































 function setValue(value)
      {
        if(value == "적정") return "true|false";
        if(value == "부적정") return "false|true";
        return "";
      }




















