function setGradeString(cus) 
            {
              if(cus == "공격투자형")  return "1";
              if(cus == "적극투자형")  return "2";
              if(cus == "위험중립형")  return "3";
              if(cus == "안정추구형")  return "4";
              if(cus == "안정형")      return "5";

              return "";
            }

