function checkToBrth(value)
        {
          var ret = value.charAt(6);
          if(ret == "1" || ret == "2" || ret == "5" || ret == "6")      return "19"+ value.substr(0,6);
          if(ret == "3" || ret == "4" || ret == "7" || ret == "8")      return "20"+ value.substr(0,6);
          return "";
        }

