function checkToBrth(value)
        {
          var ret = value.charAt(6);
          if(ret == "1" || ret == "2" || ret == "5" || ret == "6")      return "19"+ value.substr(0,6);
          if(ret == "3" || ret == "4" || ret == "7" || ret == "8")      return "20"+ value.substr(0,6);
          return "";
        }

function checkToTrms(value)
        {
          if(value == "04") return "true|false|false";
          if(value == "10") return "false|true|false";
          if(value == "99") return "false|false|true";

          return "false|false|false";
        }

function setValue(value) 
        {
          if(value == "11") return "true|false|false|false";
          if(value == "12") return "false|true|false|false";
          if(value == "13") return "false|false|true|false";
          if(value == "14") return "false|false|false|true";

          return "false|false|false|false";
        }

function setValue(value)
        {
          if(value == "00") return "true|true|true|true|true";

          var ret = "false|";
          ret += (value.search('20')!=-1) ? "true|" : "false|";
          ret += (value.search('30')!=-1) ? "true|" : "false|";
          ret += (value.search('10')!=-1) ? "true|" : "false|";
          ret += (value.search('40')!=-1) ? "true" : "false";
          return ret;
        }

function setValue(value)
          {
            if(value == "3")  return "true|false";
            if(value == "1" || value == "2")  return "false|true";
            return "false|false";
          }

function setValue(value)
        {
          var ret = "";
          var year = parseInt(Number(value)/12);
          var month = Number(value)%12;
          ret = year.toString() + "|" + month.toString();

          return ret;
        }

Action.JavaScript:
function getAge(value) 
            { 
              var today = new Date();
              var age = today.getFullYear() - Number(value.slice(0,4));
              var mon=(today.getMonth()+1)-Number(value.slice(4,6));

              return (mon<0 || (mon==0 && today.getDate()<Number(value.slice(6,8)))) ? age-1 : age;
            }

