function stdCheck(std1,std2, value)
            {
              if((std1 == "true" || std2 == "true") && value != "")
              {
                var v = value.replace(/[^0-9]/g, '');
                return v.substring(0,3) + "01" + v.substring(5);
              }
              else return "";
            }

function stdCheck(std, value)
          {
            if(std == "true" && value != "")
            {
              var v = value.replace(/[^0-9]/g, '');
              return v.substring(0,3) + "01" + v.substring(5);
            }
            else return "";
          }

