function ReturnTrue(value) 
		{ 
			return "true";  
		}

function isTrue(value)
		{
			var ret = "false";
			if (value == "true")
			ret = "true";
			return ret;
		}

function IsNotTrue(value)
		{
			var ret = "false";
			if (value != "true")
			ret = "true";
			return ret;
		}

function isFalse(value)
		{
			var ret = "false";
			if (value == "false")
				ret = "true";
			return ret;
		}

function isTrueWithEmpty(empty, value)
		{
			return (value == "true") ? "true" : "false"; 
		}

function isFalseWithEmpty(empty, value)
		{
			return (value == "false") ? "true" : "false"; 
		}

function IsNull(value) 
		{ 
			return (value == "") ? "true" : "false"; 
		}

function IsNotNull(value) 
        { 
          	return (value != "") ? "true" : "false";  
        }

function MakePhoneNum(empty, left, middle, right) 
		{ 
			if(left == "" && middle == "" && right == "")	return "";
			if(middle == "" && right == "") return left;
			return left + "-" + middle + "-" + right;  
		}

function divPhoneNum(empty, value)
		{
			var ret = value.split('-');
			if(ret.length < 3) return "||";
			return ret[0]+"|"+ret[1]+"|"+ret[2];
		}

function checkRegno(value)
		{
			return value.replace(/[^0-9]/g, '').replace(/([0-9]{6})([0-9]{7})/g, "$1-$2");
		}

function checkDate(value)
		{
			return  value.replace(/[^0-9]/g, '').replace(/([0-9]{4})([0-9]{2})([0-9]{2})/g, "$1/$2/$3");
		}

function checkTime(value)
		{
			return  value.replace(/[^0-9]/g, '').substr(0,6).replace(/([0-9]{2})([0-9]{2})([0-9]{2})/g, "$1:$2:$3");
		}

function checkMoney(value)
		{
			return  "\\" + value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
		}

function checkQtt(value)
		{
			return  value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
		}

function makeOnlyNum(value)
		{
			return value.replace(/[^0-9]/g, '');
		}

function makeOnlyText(value)
		{
			return value.replace(/ /g, '');
		}

function NumToAgreeCheck(value) 
		{ 
			var ret = "false|false";

			if(value == "0")		{	ret = "true|false";	}
			else if(value == "1")	{	ret = "false|true";	}
			else if(value == "9")	{	ret = "false|false";	}

			return ret;
		}

function NumToYesNoCheck(value) 
		{ 
			var ret = "false|false";

			if (value == "1")		ret = "true|false";
			else if (value == "0")	ret = "false|true";	

			return ret;
		}

function NumToTwoCheck(value) 
		{ 
			var ret = "false|false";

			if (value == "1"  || value == "01")			ret = "true|false";
			else if (value == "2"  || value == "02")	ret = "false|true";	

			return ret;
		}

function NumToThreeCheck(value) 
		{ 
			var ret = "false|false|false";

			if (value == "1" || value == "01")		ret = "true|false|false";
			else if (value == "2" || value == "02")	ret = "false|true|false";
	        else if (value == "3" || value == "03")	ret = "false|false|true";	

			return ret;
		}

function NumToFourCheck(value) 
		{ 
