function stdCheck(std, value)
		{
			var ret = value;
			if(std == "" || std == "false")	ret = "";
			return ret;
		}

function stdTwoCheck(std1, std2, value)
		{
			if(!(std1 == "" || std1 == "false"))	return value;
			if(!(std2 == "" || std2 == "false"))	return value;
			return "";
		}

function stdThreeCheck(std1, std2, std3, value)
		{
			if(!(std1 == "" || std1 == "false"))	return value;
			if(!(std2 == "" || std2 == "false"))	return value;
			if(!(std3 == "" || std3 == "false"))	return value;
			return "";
		}

function stdFourCheck(std1, std2, std3, std4, value)
		{
